DROP TABLE IF EXISTS `cmswing_model`;
CREATE TABLE `cmswing_model` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '模型ID',
  `name` char(30) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '模型标识',
  `title` char(30) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '模型名称',
  `extend` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '继承的模型',
  `relation` varchar(30) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '继承与被继承模型的关联字段',
  `need_pk` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '新建表时是否需要主键字段',
  `field_sort` text CHARACTER SET utf8 COMMENT '表单字段排序',
  `field_group` varchar(255) CHARACTER SET utf8 NOT NULL DEFAULT '1:基础' COMMENT '字段分组',
  `attribute_list` text CHARACTER SET utf8 COMMENT '属性列表（表的字段）',
  `attribute_alias` varchar(255) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '属性别名定义',
  `template_list` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '列表模板',
  `template_add` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '新增模板',
  `template_edit` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '编辑模板',
  `list_grid` text CHARACTER SET utf8 COMMENT '列表定义',
  `list_row` smallint(2) unsigned NOT NULL DEFAULT '10' COMMENT '列表数据长度',
  `search_key` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '默认搜索字段',
  `search_list` varchar(255) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '高级搜索的字段',
  `create_time` bigint(15) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` bigint(15) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `status` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '状态',
  `engine_type` varchar(25) CHARACTER SET utf8 NOT NULL DEFAULT 'MyISAM' COMMENT '数据库引擎',
  `key_show` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0不在话题显示，1在话题显示',
  `temp_show` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1 启用模版，0不启用模版',
  `type_show` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1启用分类信息，0不启用分类信息',
  `priv_show` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1开启权限，0不开启权限。',
  `groups_show` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1开启分组，0不开启分组',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=DYNAMIC COMMENT='文档模型表';

