INSERT INTO `cmswing_temp` VALUES ('1', 'topic', 'index', 'index', '首页模版', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<link href=\"/static/assets/css/layout-question.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}\n{% block content %}\n<!-- SLIDER -->\n<section id=\"slider\" class=\"\" style=\"background:url(\'/static/assets/images/index.jpg\');height: 500px;background-position: center center\">\n	<span class=\"overlay dark-2\"><!-- dark overlay [0 to 9 opacity] --></span>\n\n	<div class=\"display-table\">\n		<div class=\"display-table-cell vertical-align-middle\">\n\n			<div class=\"container text-center\">\n\n				<h1 class=\"nomargin wow fadeInUp\" data-wow-delay=\"0.4s\">\n					WE ARE\n					<!--\n                        TEXT ROTATOR\n                        data-animation=\"fade|flip|flipCube|flipUp|spin\"\n                    -->\n					<span class=\"rotate \" data-animation=\"fade\" data-speed=\"1500\">\n									CMSWING, MULTIPURPOSE, POWERFUL\n								</span>\n				</h1>\n\n				<p class=\"lead size-20 wow fadeInUp\" data-wow-delay=\"0.7s\">\n					一款基于ThinkJS的功能强大的（PC端,手机端和微信公众平台）电子商务平台及CMS建站系统\n					<!--Over <span class=\"countTo\" data-speed=\"4000\">550</span> html files!-->\n					<!--<span class=\"theme-color weight-400 font-style-italic\">Admin included</span> &amp; MLT-->\n				</p>\n\n				<a class=\"btn btn-default btn-lg\" href=\"/documentation\">Getting Started</a>\n			</div>\n\n		</div>\n	</div>\n\n</section>\n<!-- /SLIDER -->\n<!-- INFO BAR -->\n<section class=\"info-bar info-bar-clean \">\n<div class=\"container\">\n\n<div class=\"row\">\n\n<div class=\"col-sm-3 padding-top-0 padding-bottom-0\">\n<i><img src=\"http://git.oschina.net/logo.gif\" style=\"width:32px; margin-top: -2px\"></i>\n<h3>码云主页</h3>\n<p>去 <a href=\"http://git.oschina.net/arterli/CmsWing\" target=\"_blank\">码云</a> 查看最新的开发进度</p>\n</div>\n\n<div class=\"col-sm-3 padding-top-0 padding-bottom-0\">\n<i class=\"glyphicon glyphicon-download-alt\"></i>\n<h3>下载CmsWing</h3>\n	{% topic data = \"lastd\",limit= \"1\",cid=\"111\",position=\"1\",isstu=\"1\",issub=\"2\",group=\"1\"%}\n	{%set vv = lastd[0].title|strToArray(\"@\")%}\n<p><a href=\"/download\">下载最新版本</a> {{vv[1]}} 下载次数:{{lastd[0].download}}</p>\n</div>\n\n<div class=\"col-sm-3 padding-top-0 padding-bottom-0\">\n<i><img src=\"/static/logo_32x32.png\" style=\"width:32px; margin-top: -2px\"></i>\n<h3>交流学习</h3>\n<p><a href=\"/question\">社区</a> QQ群:49757468</p>\n</div>\n	<div class=\"col-sm-3 padding-top-0 padding-bottom-0\">\n		<i><img src=\"/static/thinkjslogo_32.png\" style=\"width:32px; margin-top: -2px\"></i>\n		<h3>ThinkJS</h3>\n		<p><a href=\"https://thinkjs.org\" target=\"_blank\">官方网站</a> QQ群:339337680</p>\n	</div>\n\n</div>\n\n</div>\n</section>\n<!-- /INFO BAR -->\n<!-- RECENT NEWS -->\n<section>\n<div class=\"container\">\n\n<header class=\"text-center margin-bottom-20\">\n	<div class=\"heading-title heading-line-double text-center margin-bottom-0\"><h3>示例/教程</h3>\n		</div>\n\n</header>\n\n<!--\ncontrolls-over		= navigation buttons over the image\nbuttons-autohide 	= navigation buttons visible on mouse hover only\n\ndata-plugin-options:\n\"singleItem\": true\n\"autoPlay\": true (or ms. eg: 4000)\n\"navigation\": true\n\"pagination\": true\n\"items\": \"4\"\n\nowl-carousel item paddings\n.owl-padding-0\n.owl-padding-3\n.owl-padding-6\n.owl-padding-10\n.owl-padding-15\n.owl-padding-20\n-->\n<div class=\"owl-carousel owl-padding-10 buttons-autohide controlls-over\" data-plugin-options=\'{\"singleItem\": false, \"items\":\"4\", \"autoPlay\": 4000, \"navigation\": true, \"pagination\": false}\'>\n	{% topic data = \"indexnew\",cid=\"46\",limit=\"12\",ispic=\"1\"%}\n	{%for v in indexnew%}\n<div class=\"img-hover\">\n<a href=\"{{v.name|get_url(v.id)}}\" target=\"_blank\">\n<img class=\"img-responsive\" src=\"{{v.cover_id|get_pic(\'m=1,w=256,h=126\')}}\" alt=\"\">\n</a>\n<h5 class=\"text-left margin-top-10 margin-bottom-2\"><a href=\"{{v.name|get_url(v.id)}}\" target=\"_bl